package AssignmentControlFlowStatements;
/*
 * 
1
2 2
3 3 3
4 4 4 4
 */
public class Que15b {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=0; i<4; i++) {
			for(int j=1; j<=i+1; j++) {
				System.out.print(i+1);
			}
			System.out.println();
		}
		
		
	}

}
